            .clickable { isChecked = !isChecked },
        contentAlignment = Alignment.Center
    ) {
        Checkbox(
            checked = isChecked,
            onCheckedChange = null,
            modifier = Modifier.scale(scale),
            colors = CheckboxDefaults.colors(
                checkedColor = color,
                uncheckedColor = color
            )
        )
    }
}
