## Adding Navigation to the Jetpack Compose Navigation Drawer

A Jetpack Compose navigation drawer is most useful when integrated with the Navigation Component. Let's implement a navigation drawer with actual screen navigation:

```kotlin
import androidx.compose.foundation.layout.*
import androidx.compose.material.*
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController
import kotlinx.coroutines.launch

@Composable
fun NavigationDrawerWithNavigation() {
    val scaffoldState = rememberScaffoldState()
    val scope = rememberCoroutineScope()
    val navController = rememberNavController()
    
    // Track the current screen to update the top bar title
    var currentScreen by remember { mutableStateOf("Home") }
    
    Scaffold(
        scaffoldState = scaffoldState,
        topBar = {
            TopAppBar(
                title = { Text(currentScreen) },
                navigationIcon = {
                    IconButton(onClick = {
                        scope.launch {
                            scaffoldState.drawerState.open()
                        }
                    }) {
                        Icon(Icons.Filled.Menu, contentDescription = "Menu")
                    }
                }
            )
        },
        drawerContent = {
            DrawerHeader()
            DrawerBody(
                navController = navController,
                scaffoldState = scaffoldState,
                scope = scope,
                onDestinationChanged = { screen ->
                    currentScreen = screen
                }
            )
        },
        content = { paddingValues ->
            NavHost(
                navController = navController,
                startDestination = "home",
                modifier = Modifier.padding(paddingValues)
            ) {
                composable("home") {
                    HomeScreen()
                }
                composable("profile") {
                    ProfileScreen()
                }
                composable("settings") {
                    SettingsScreen()
                }
                composable("help") {
                    HelpScreen()
                }
            }
        }
    )
}

@Composable
fun DrawerHeader() {
    Box(
        modifier = Modifier
            .fillMaxWidth()
            .height(180.dp)
            .padding(vertical = 16.dp),
        contentAlignment = Alignment.Center
    ) {
        Column(horizontalAlignment = Alignment.CenterHorizontally) {
            // You can add an app logo or user profile image here
            Surface(
                modifier = Modifier.size(80.dp),
                shape = MaterialTheme.shapes.circle,
                color = MaterialTheme.colors.primary
            ) {
                // Placeholder for logo/profile image
            }
            
            Spacer(modifier = Modifier.height(16.dp))
            
            Text(
                text = "My Compose App",
                style = MaterialTheme.typography.h6
            )
            
            Text(
                text = "example@email.com",
                style = MaterialTheme.typography.body2
            )
        }
    }
    
    Divider()
}

@Composable
fun DrawerBody(
    navController: NavController,
    scaffoldState: ScaffoldState,
    scope: CoroutineScope,
    onDestinationChanged: (String) -> Unit
) {
    val menuItems = listOf(
        MenuItem(
            title = "Home",
            icon = Icons.Default.Home,
            route = "home"
        ),
        MenuItem(
            title = "Profile",
            icon = Icons.Default.Person,
            route = "profile"
        ),
        MenuItem(
            title = "Settings",
            icon = Icons.Default.Settings,
            route = "settings"
        ),
        MenuItem(
            title = "Help",
            icon = Icons.Default.Info,
            route = "help"
        )
    )
    
    Column(modifier = Modifier.padding(16.dp)) {
        menuItems.forEach { item ->
            DrawerItem(
                icon = item.icon,
                label = item.title,
                onClick = {
                    navController.navigate(item.route) {
                        // Pop up to the start destination of the graph to
                        // avoid building up a large stack of destinations
                        popUpTo(navController.graph.startDestinationId)
                        // Avoid multiple copies of the same destination when
                        // reselecting the same item
                        launchSingleTop = true
                    }
                    
                    // Update the current screen title
                    onDestinationChanged(item.title)
                    
                    // Close the drawer
                    scope.launch {
                        scaffoldState.drawerState.close()
                    }
                }
            )
            
            Spacer(modifier = Modifier.height(16.dp))
        }
    }
}

